mapper.select:
select od.orderid, c.comp_nm as compNm, s.sales_nm as salesNm, oi.order_cnt as orderCnt, i.unitprice, date_format(p.purdate, '%Y-%m-%d') as purdate, od.confirmYN from tb_orderinfo oi inner join tb_comp c on c.comp_id = oi.comp_id inner join tb_orderdir od on od.order_id = oi.order_id inner join tb_sales s on oi.sales_id = s.sales_id inner join tb_import i on i.sales_id = s.sales_id and c.comp_id = i.comp_id inner join tb_purchase p on p.loginID = oi.loginID and p.pur_id = oi.pur_id WHERE 1=1
AND s.sales_nm LIKE CONCAT('%', #{salesNm}, '%')
AND DATE_FORMAT(p.purdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(p.purdate, '%Y-%m-%d') <= #{to_date}
AND confirmYN is null
ORDER BY p.purdate ASC LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.study.system.dao.orderConfirmDao.orderConfirmCnt*/ SELECT count(*) from tb_orderinfo oi inner join tb_comp c on c.comp_id = oi.comp_id inner join tb_orderdir od on od.order_id = oi.order_id inner join tb_sales s on oi.sales_id = s.sales_id inner join tb_import i on i.sales_id = s.sales_id and c.comp_id = i.comp_id inner join tb_purchase p on p.loginID = oi.loginID and p.pur_id = oi.pur_id
AND s.sales_nm LIKE CONCAT('%', #{salesNm}, '%')
AND DATE_FORMAT(p.purdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(p.purdate, '%Y-%m-%d') <= #{to_date}
AND confirmYN is null

mapper.update:
/*kr.happy.study.system.dao.orderConfirmDao.updateorderConfirm*/
UPDATE tb_orderdir SET
      confirmYN = #{confirmYN}
WHERE   orderid = #{orderid}
