mapper.select:
/*kr.happy.study.pcm.dao.PcmReturnListDao.listPurchaseReturn*/ SELECT rd.return_id , cp.comp_nm , sa.sales_nm , rd.return_cnt , rd.regdate , rd.confirmYN
FROM tb_returndir rd inner join tb_orderdir od on od.orderid = rd.orderid inner join tb_orderinfo oi on oi.order_id = od.order_id inner join tb_sales sa on sa.sales_id = oi.sales_id inner join tb_comp cp on oi.comp_id = cp.comp_id WHERE 1=1
AND cp.comp_nm LIKE CONCAT('%', #{sname}, '%')
AND sa.sales_nm LIKE CONCAT('%', #{sname}, '%')
AND rd.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND rd.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') >= #{cal1}
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') <= #{cal2}
AND rd.confirmYN = 'Y'
LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.study.pcm.dao.PcmReturnListDao.totalCount*/ SELECT count(*) as cnt
FROM tb_returndir rd inner join tb_orderdir od on od.orderid = rd.orderid inner join tb_orderinfo oi on oi.order_id = od.order_id inner join tb_sales sa on sa.sales_id = oi.sales_id inner join tb_comp cp on oi.comp_id = cp.comp_id WHERE 1=1
AND cp.comp_nm LIKE CONCAT('%', #{sname}, '%')
AND sa.sales_nm LIKE CONCAT('%', #{sname}, '%')
AND rd.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND rd.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') >= #{cal1}
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') <= #{cal2}
AND rd.confirmYN = 'Y'
