mapper.select:
/*kr.happy.study.pcm.dao.PcmOrderListDao.listPurchaseOrder*/ SELECT od.orderid , cp.comp_nm , sa.sales_nm , oi.order_cnt , od.regdate , od.confirmYN , ph.depositYN
FROM tb_orderdir od inner join tb_orderinfo oi on oi.order_id = od.order_id inner join tb_sales sa on sa.sales_id = oi.sales_id inner join tb_detail_code dc on dc.detail_code = sa.sales_type and dc.group_code = 'proCD' inner join tb_comp cp on oi.comp_id = cp.comp_id inner join tb_purchase ph on ph.pur_id = oi.pur_id WHERE 1=1
AND cp.comp_nm LIKE CONCAT('%', #{sname}, '%')
AND sa.sales_nm LIKE CONCAT('%', #{sname}, '%')
AND od.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND od.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') >= #{cal1}
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') <= #{cal2}
AND confirmYN = 'Y'
LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.study.pcm.dao.PcmOrderListDao.totalCount*/ SELECT count(*) as cnt
FROM tb_orderdir od inner join tb_orderinfo oi on oi.order_id = od.order_id inner join tb_sales sa on sa.sales_id = oi.sales_id inner join tb_detail_code dc on dc.detail_code = sa.sales_type and dc.group_code = 'proCD' inner join tb_comp cp on oi.comp_id = cp.comp_id WHERE 1=1
AND cp.comp_nm LIKE CONCAT('%', #{sname}, '%')
AND sa.sales_nm LIKE CONCAT('%', #{sname}, '%')
AND od.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND od.confirmYN LIKE CONCAT('%', #{sname}, '%')
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') >= #{cal1}
AND DATE_FORMAT(od.regdate, '%Y-%m-%d') <= #{cal2}
AND confirmYN = 'Y'
