mapper.select:
/* kr.happyjob.study.shipping.dao.ShippingDao.listDeliOrder */ SELECT dd.deliv_id, dd.regdate , dd.deldate, dd.editdate , dd.deliverStatus, dd.purinf_id , pi.pur_cnt, s.sales_nm, ui.name , dd.deliv_wh_id
FROM tb_delivdir dd LEFT JOIN tb_purchaseinfo pi ON dd.purinf_id = pi.purinf_id LEFT JOIN tb_sales s ON pi.sales_id = s.sales_id LEFT JOIN tb_purchase p ON pi.pur_id = p.pur_id LEFT JOIN tb_userinfo ui ON p.loginID = ui.loginID
WHERE pi.returnYN='N'
and s.sales_nm LIKE CONCAT('%', #{sname}, '%')
and dd.deliverStatus= #{oname}
and dd.regdate >= STR_TO_DATE(#{cal1},'%Y-%m-%d')
and dd.regdate <= DATE_ADD(STR_TO_DATE(#{cal2},'%Y-%m-%d') , INTERVAL 1 DAY)
ORDER BY deliv_id DESC LIMIT #{pageIndex}, #{pageSize}

/* kr.happyjob.study.shipping.dao.ShippingDao.countListDeliOrder */ SELECT count(1) AS tot_cnt FROM tb_delivdir dd LEFT JOIN tb_purchaseinfo pi ON dd.purinf_id = pi.purinf_id LEFT JOIN tb_sales s ON pi.sales_id = s.sales_id LEFT JOIN tb_purchase p ON pi.pur_id = p.pur_id LEFT JOIN tb_userinfo ui ON p.loginID = ui.loginID
WHERE pi.returnYN='N'
and s.sales_nm LIKE CONCAT('%', #{sname}, '%')
and dd.deliverStatus= #{oname}
and dd.regdate >= STR_TO_DATE(#{cal1},'%Y-%m-%d')
and dd.regdate <= DATE_ADD(STR_TO_DATE(#{cal2},'%Y-%m-%d') , INTERVAL 1 DAY)

mapper.update:
/* kr.happyjob.study.shipping.dao.ShippingDao.updateDeliOrder */
UPDATE tb_delivdir 
   SET deliverStatus = 'E'
	  , editdate = NOW()
 WHERE deliv_id = #{deliv_id}

/* kr.happyjob.study.shipping.dao.ShippingDao.updateStockReduce */
UPDATE tb_stock
   SET st_cnt = st_cnt - #{pur_cnt}
 WHERE sales_id = #{sales_id} AND wh_id = #{wh_id}
